import CMigration
import Darwin

let BASEMODE = S_IRUSR | S_IWUSR | S_IRGRP | S_IWGRP | S_IROTH | S_IWOTH

@main struct mkfifo : ShellCommand {

  struct CommandOptions {
    var f_mode = false
    var modestr : String = ""
    var args = [String]()
  }

  var options : CommandOptions!

  func parseOptions() throws(CmdErr) -> CommandOptions {
    var options = CommandOptions()
    let go = BSDGetopt("m:")
    while let (k,v) = try go.getopt() {
      switch(k) {
        case "m":
          options.f_mode = true
          options.modestr = v
        case "?":
          fallthrough
        default:
          throw CmdErr(1)
      }
    }
    options.args = go.remaining
    if options.args.isEmpty {
      throw CmdErr(1)
    }
    return options
  }

  func runCommand() async throws(CmdErr) {
    let unix2003compat = true // COMPAT_MODE("bin/mkfifo", "Unix2003");

    var fifomode : UInt16 = 0
    /* The default mode is the value of the bitwise inclusive or of
     S_IRUSR, S_IWUSR, S_IRGRP, S_IWGRP, S_IROTH, and S_IWOTH
     modified by the file creation mask */

//    if (!unix2003compat) {
//      fifomode = BASEMODE & ~umask(0)
//    }

    if options.f_mode {
      // #ifndef __APPLE__
      // 		umask(0);
      // #endif
      errno = 0;

      guard let modep = setmode(options.modestr) else {
        if (errno != 0) {
          err(1, "setmode")
        }
        errx(1, "invalid file mode: \(options.modestr)")
        fatalError()
      }

      fifomode = getmode(modep, BASEMODE)
      //#ifndef __APPLE__
      //	} else {
      //		fifomode = BASEMODE;
      //#endif
    }

    // #ifdef __APPLE__
    if (unix2003compat) {
      let maskbits = umask(0);	/* now must disable umask so -m mode won't be masked again */
      if (!options.f_mode) {
        fifomode = BASEMODE & ~maskbits;
      }
    }
    // #endif

    var exitval : Int32 = 0
    for argv in options.args {
      if (Darwin.mkfifo(argv, fifomode) < 0) {
