/*
 * pathchk -- check pathnames
 *
 * Check whether files could be created with the names specified on the
 * command line. If -p is specified, check whether the pathname is portable
 * to all POSIX systems.
 */

import CMigration
import Darwin

@main struct pathchk : ShellCommand {

  struct CommandOptions {
    var pflag = false  /* Perform portability checks */
    var Pflag = false  /* Check for empty paths, leading '-' */
    var args : [String] = []
  }

  var options : CommandOptions!

