// From LibC

import CMigration

func strmode(_ mode : FileType, _ perm : FilePermissions) -> String {
   /* print type */
  var result = ""
  switch mode {
    case .directory:      /* directory */
      result.append("d")
    case .characterDevice:      /* character special */
      result.append("c")
    case .blockDevice:      /* block special */
      result.append("b")
    case .regular:      /* regular */
      result.append("-")
    case .symbolicLink:      /* symbolic link */
      result.append("l")
    case .socket:      /* socket */
      result.append("s")
    case .fifo:        /* fifo */
      result.append("p")
    case .whiteOut:      /* whiteout */
      result.append("w")
  default:      /* unknown */
      result.append("?")
  }
  /* usr */
  result.append( perm.contains(.ownerRead) ? "r" : "-")
  result.append( perm.contains(.ownerWrite) ? "w" : "-")

  if perm.contains(.ownerExecute) && perm.contains(.setUserID) {
    result.append("s")
  } else if perm.contains(.ownerExecute) {
    result.append("x")
  } else if perm.contains(.setUserID) {
    result.append("S")
  } else {
    result.append("-")
  }

  /* group */
  result.append( perm.contains(.groupRead) ? "r" : "-")
  result.append( perm.contains(.groupWrite) ? "w" : "-")
  
  if perm.contains(.groupExecute) && perm.contains(.setGroupID) {
    result.append("s")
  } else if perm.contains(.groupExecute) {
    result.append("x")
  } else if perm.contains(.setGroupID) {
    result.append("S")
  } else {
    result.append("-")
  }

  /* other */
  result.append( perm.contains(.otherRead) ? "r" : "-")
  result.append( perm.contains(.otherWrite) ? "w" : "-")

  if perm.contains(.otherExecute) && perm.contains(.saveText) {
    result.append("t")
  } else if perm.contains(.otherExecute) {
    result.append("x")
  } else if perm.contains( .saveText) {
    result.append("T")
  } else {
    result.append("-")
  }
  result.append(" ")
  return result
}

