/*
 * fflagstostr --
 *  Convert file flags to a comma-separated string.  If no flags
 *  are set, return the empty string.
 */
func fflagstostr( _ flags : FileFlags) -> String? {
  var rmap : [FileFlags : String]
  for (k, v) in setMapping {
    if let j = rmap[v] {
      if j.count > k.count { rmap[v]=k }
    } else {
      rmap[v]=k
    }
  }

  let setflags = flags.allFlags
  var dp = ""
  for f in setflags  {
    if let j = rmap[f] {
      if !dp.isEmpty {
        dp.append(",")
      }
      dp.append(j)
    }
  }
  return dp
}

/*
 * strtofflags --
 *  Take string of arguments and return file flags.  Return 0 on
 *  success, 1 on failure.  On failure, stringp is set to point
 *  to the offending token.
 */
func strtofflags(_ stringp : String) -> (FileFlags, FileFlags)? {
  var setp = FileFlags()
  var clrp = FileFlags()

  let ss = stringp.split(separator: "\t", omittingEmptySubsequences: true)
  for pp in ss {
    let p = String(pp)
    if let i = setMapping[p] {
      setp.insert(i)
    } else if let i = clrMapping[p] {
      clrp.insert(i)
    } else {
      return nil
    }
  }
  return (setp, clrp)
}
