import CMigration
import Darwin

@main struct rmdir : ShellCommand {

  struct CommandOptions {
    var pflag = false
    var vflag = false
    var args = [String]()
  }

  func parseOptions() throws(CmdErr) -> CommandOptions {
    var opts = CommandOptions()
    let go = BSDGetopt("pv")
    while let (k,_) = try go.getopt() {
      switch k {
        case "p":
          opts.pflag = true
        case "v":
          opts.vflag = true
        case "?":
          fallthrough
        default:
          throw CmdErr(1)
      }
    }
    opts.args = go.remaining

    if opts.args.isEmpty  {
      throw CmdErr(1)
    }
    return opts
  }

  func runCommand(_ opts : CommandOptions) async throws(CmdErr) {
    var errors = false
    for argv in opts.args {
      if Darwin.rmdir(argv) < 0 {
        warnx(argv)
        errors = true
      } else {
        if opts.vflag {
          print(argv)
        }
        if opts.pflag {
          errors = rm_path(argv, opts.vflag) || errors
        }
      }
    }
    exit(errors ? 1 : 0);
  }

  func rm_path(_ pp : String, _ verbose : Bool) -> Bool  {
    var path = pp.split(separator: "/", omittingEmptySubsequences: false)

    while !path.isEmpty {
      /* Delete trailing slashes. */
      while path.last?.isEmpty == true { path.removeLast() }
      if path.isEmpty { break }


      let pp = path.joined(separator: "/")
      if Darwin.rmdir(pp) < 0 {
        warnx(pp)
        return true
      }
      if verbose {
        print(path)
      }
      path.removeLast()
    }

    return false
  }

  var usage = "usage: rmdir [-pv] directory ..."
}
